module.exports = function (grunt) {
	'use strict';

	require('load-grunt-tasks')(grunt);

	grunt.initConfig({
		jshint: {
			options: {
				'jshintrc': '../.jshintrc',
        'reporter': require('jshint-stylish')
			},
			gruntfile: 'Gruntfile.js',
			bin: {
				src: [
					'index.js',
					'lib/**/*.js'
				]
			}
		},
		mochacli: {
			options: {
				reporter: 'nyan',
				bail: true
			},
			all: ['test/*.js']
		},
		watch: {
			files: [
				'Gruntfile.js',
				'<%= jshint.bin.src %>'
			],
			tasks: [
				'jshint'
			]
		},
		clean: {
			modules: ['node_modules']
		}
	});

	grunt.registerTask('default', ['jshint']);
	grunt.registerTask('test', ['mochacli']);
};
